import React, { useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Button,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import {
  Save as SaveIcon,
  Print as PrintIcon,
  Download as DownloadIcon,
  ArrowBack as ArrowBackIcon,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';

interface PlanData {
  basicInfo: {
    name: string;
    birthDate: string;
    gender: string;
    address: string;
    phone: string;
    emergencyContact: string;
    disabilityType: string;
    supportLevel: string;
    education: string;
  };
  currentSituation: {
    livingSituation: string;
    physicalCondition: string;
    mentalCondition: string;
    socialParticipation: string;
    familySituation: string;
  };
  goals: {
    shortTerm: string;
    longTerm: string;
    achievementIndicators: string;
  };
  services: {
    serviceType: string;
    frequency: string;
    duration: string;
    provider: string;
    startDate: string;
  };
  supportSystem: {
    supportCoordinator: string;
    serviceProviders: string;
    relatedInstitutions: string;
    informalSupport: string;
  };
  monitoring: {
    frequency: string;
    evaluationMethod: string;
    reviewDate: string;
    emergencyResponse: string;
  };
}

const steps = [
  '基本情報',
  '現状・アセスメント',
  '支援目標',
  'サービス利用計画',
  '支援体制',
  'モニタリング計画',
];

const PlanEditor: React.FC = () => {
  const { planId } = useParams<{ planId: string }>();
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [planData, setPlanData] = useState<PlanData>({
    basicInfo: {
      name: '田中太郎',
      birthDate: '1985-03-15',
      gender: '男性',
      address: '東京都渋谷区○○町1-2-3',
      phone: '03-1234-5678',
      emergencyContact: '田中花子（母） 090-1234-5678',
      disabilityType: '身体障害',
      supportLevel: '区分2',
      education: '高卒',
    },
    currentSituation: {
      livingSituation: '一人暮らし（アパート）',
      physicalCondition: '車椅子を使用、上肢に軽度の麻痺あり',
      mentalCondition: '安定している',
      socialParticipation: '地域のボランティア活動に参加',
      familySituation: '両親は健在、定期的に連絡',
    },
    goals: {
      shortTerm: '6ヶ月以内に自立した生活を送る',
      longTerm: '1年後に就労を目指す',
      achievementIndicators: '日常生活動作の自立度向上',
    },
    services: {
      serviceType: '居宅介護',
      frequency: '週3回',
      duration: '2時間/回',
      provider: '○○介護サービス',
      startDate: '2024-02-01',
    },
    supportSystem: {
      supportCoordinator: '山田相談員',
      serviceProviders: '○○介護サービス、△△医療センター',
      relatedInstitutions: '区役所福祉課、地域包括支援センター',
      informalSupport: '近隣住民、ボランティア',
    },
    monitoring: {
      frequency: '月1回',
      evaluationMethod: '面談、アンケート',
      reviewDate: '2024-07-01',
      emergencyResponse: '24時間対応可能',
    },
  });

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleSave = () => {
    // 保存処理
    console.log('Saving plan:', planData);
  };

  const handlePrint = () => {
    // 印刷処理
    window.print();
  };

  const handleDownload = () => {
    // PDFダウンロード処理
    console.log('Downloading plan as PDF');
  };

  const updatePlanData = (section: keyof PlanData, field: string, value: string) => {
    setPlanData((prev) => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value,
      },
    }));
  };

  const renderBasicInfo = () => (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="氏名"
          value={planData.basicInfo.name}
          onChange={(e) => updatePlanData('basicInfo', 'name', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="生年月日"
          type="date"
          value={planData.basicInfo.birthDate}
          onChange={(e) => updatePlanData('basicInfo', 'birthDate', e.target.value)}
          InputLabelProps={{ shrink: true }}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <FormControl fullWidth>
          <InputLabel>性別</InputLabel>
          <Select
            value={planData.basicInfo.gender}
            onChange={(e) => updatePlanData('basicInfo', 'gender', e.target.value)}
            label="性別"
          >
            <MenuItem value="男性">男性</MenuItem>
            <MenuItem value="女性">女性</MenuItem>
            <MenuItem value="その他">その他</MenuItem>
          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="電話番号"
          value={planData.basicInfo.phone}
          onChange={(e) => updatePlanData('basicInfo', 'phone', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="住所"
          value={planData.basicInfo.address}
          onChange={(e) => updatePlanData('basicInfo', 'address', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="緊急連絡先"
          value={planData.basicInfo.emergencyContact}
          onChange={(e) => updatePlanData('basicInfo', 'emergencyContact', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <FormControl fullWidth>
          <InputLabel>障害種別</InputLabel>
          <Select
            value={planData.basicInfo.disabilityType}
            onChange={(e) => updatePlanData('basicInfo', 'disabilityType', e.target.value)}
            label="障害種別"
          >
            <MenuItem value="身体障害">身体障害</MenuItem>
            <MenuItem value="知的障害">知的障害</MenuItem>
            <MenuItem value="精神障害">精神障害</MenuItem>
            <MenuItem value="発達障害">発達障害</MenuItem>
            <MenuItem value="その他">その他</MenuItem>
          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={12} sm={6}>
        <FormControl fullWidth>
          <InputLabel>支援区分</InputLabel>
          <Select
            value={planData.basicInfo.supportLevel}
            onChange={(e) => updatePlanData('basicInfo', 'supportLevel', e.target.value)}
            label="支援区分"
          >
            <MenuItem value="区分1">区分1</MenuItem>
            <MenuItem value="区分2">区分2</MenuItem>
            <MenuItem value="区分3">区分3</MenuItem>
            <MenuItem value="区分4">区分4</MenuItem>
            <MenuItem value="区分5">区分5</MenuItem>
            <MenuItem value="区分6">区分6</MenuItem>
          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={12} sm={6}>
        <FormControl fullWidth>
          <InputLabel>最終学歴</InputLabel>
          <Select
            value={planData.basicInfo.education}
            onChange={(e) => updatePlanData('basicInfo', 'education', e.target.value)}
            label="最終学歴"
          >
            <MenuItem value="高卒">高卒</MenuItem>
            <MenuItem value="大卒">大卒</MenuItem>
            <MenuItem value="特別支援卒">特別支援卒</MenuItem>
          </Select>
        </FormControl>
      </Grid>
    </Grid>
  );

  const renderCurrentSituation = () => (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="現在の生活状況"
          multiline
          rows={3}
          value={planData.currentSituation.livingSituation}
          onChange={(e) => updatePlanData('currentSituation', 'livingSituation', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="身体機能・精神状態"
          multiline
          rows={3}
          value={planData.currentSituation.physicalCondition}
          onChange={(e) => updatePlanData('currentSituation', 'physicalCondition', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="社会参加の状況"
          multiline
          rows={3}
          value={planData.currentSituation.socialParticipation}
          onChange={(e) => updatePlanData('currentSituation', 'socialParticipation', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="家族・支援者の状況"
          multiline
          rows={3}
          value={planData.currentSituation.familySituation}
          onChange={(e) => updatePlanData('currentSituation', 'familySituation', e.target.value)}
        />
      </Grid>
    </Grid>
  );

  const renderGoals = () => (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="短期目標（6ヶ月以内）"
          multiline
          rows={3}
          value={planData.goals.shortTerm}
          onChange={(e) => updatePlanData('goals', 'shortTerm', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="長期目標（1年以上）"
          multiline
          rows={3}
          value={planData.goals.longTerm}
          onChange={(e) => updatePlanData('goals', 'longTerm', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="具体的な達成指標"
          multiline
          rows={3}
          value={planData.goals.achievementIndicators}
          onChange={(e) => updatePlanData('goals', 'achievementIndicators', e.target.value)}
        />
      </Grid>
    </Grid>
  );

  const renderServices = () => (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6}>
        <FormControl fullWidth>
          <InputLabel>利用サービス種別</InputLabel>
          <Select
            value={planData.services.serviceType}
            onChange={(e) => updatePlanData('services', 'serviceType', e.target.value)}
            label="利用サービス種別"
          >
            <MenuItem value="居宅介護">居宅介護</MenuItem>
            <MenuItem value="重度訪問介護">重度訪問介護</MenuItem>
            <MenuItem value="同行援護">同行援護</MenuItem>
            <MenuItem value="行動援護">行動援護</MenuItem>
            <MenuItem value="療養介護">療養介護</MenuItem>
            <MenuItem value="生活介護">生活介護</MenuItem>
            <MenuItem value="就労継続支援">就労継続支援</MenuItem>
            <MenuItem value="就労移行支援">就労移行支援</MenuItem>
          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="頻度"
          value={planData.services.frequency}
          onChange={(e) => updatePlanData('services', 'frequency', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="時間・期間"
          value={planData.services.duration}
          onChange={(e) => updatePlanData('services', 'duration', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="提供事業者"
          value={planData.services.provider}
          onChange={(e) => updatePlanData('services', 'provider', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="利用開始予定日"
          type="date"
          value={planData.services.startDate}
          onChange={(e) => updatePlanData('services', 'startDate', e.target.value)}
          InputLabelProps={{ shrink: true }}
        />
      </Grid>
    </Grid>
  );

  const renderSupportSystem = () => (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="相談支援事業者"
          value={planData.supportSystem.supportCoordinator}
          onChange={(e) => updatePlanData('supportSystem', 'supportCoordinator', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="サービス提供事業者"
          multiline
          rows={3}
          value={planData.supportSystem.serviceProviders}
          onChange={(e) => updatePlanData('supportSystem', 'serviceProviders', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="関係機関・専門職"
          multiline
          rows={3}
          value={planData.supportSystem.relatedInstitutions}
          onChange={(e) => updatePlanData('supportSystem', 'relatedInstitutions', e.target.value)}
        />
      </Grid>
      <Grid item xs={12}>
        <TextField
          fullWidth
          label="家族・インフォーマル支援"
          multiline
          rows={3}
          value={planData.supportSystem.informalSupport}
          onChange={(e) => updatePlanData('supportSystem', 'informalSupport', e.target.value)}
        />
      </Grid>
    </Grid>
  );

  const renderMonitoring = () => (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="モニタリング実施頻度"
          value={planData.monitoring.frequency}
          onChange={(e) => updatePlanData('monitoring', 'frequency', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="評価方法・指標"
          value={planData.monitoring.evaluationMethod}
          onChange={(e) => updatePlanData('monitoring', 'evaluationMethod', e.target.value)}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="計画見直し予定日"
          type="date"
          value={planData.monitoring.reviewDate}
          onChange={(e) => updatePlanData('monitoring', 'reviewDate', e.target.value)}
          InputLabelProps={{ shrink: true }}
        />
      </Grid>
      <Grid item xs={12} sm={6}>
        <TextField
          fullWidth
          label="緊急時対応"
          value={planData.monitoring.emergencyResponse}
          onChange={(e) => updatePlanData('monitoring', 'emergencyResponse', e.target.value)}
        />
      </Grid>
    </Grid>
  );

  const getStepContent = (step: number) => {
    switch (step) {
      case 0:
        return renderBasicInfo();
      case 1:
        return renderCurrentSituation();
      case 2:
        return renderGoals();
      case 3:
        return renderServices();
      case 4:
        return renderSupportSystem();
      case 5:
        return renderMonitoring();
      default:
        return 'Unknown step';
    }
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">
          支援利用計画書編集
        </Typography>
        <Box>
          <Button
            startIcon={<ArrowBackIcon />}
            onClick={() => navigate('/plans')}
            sx={{ mr: 1 }}
          >
            戻る
          </Button>
          <Button
            startIcon={<SaveIcon />}
            variant="contained"
            onClick={handleSave}
            sx={{ mr: 1 }}
          >
            保存
          </Button>
          <Button
            startIcon={<PrintIcon />}
            onClick={handlePrint}
            sx={{ mr: 1 }}
          >
            印刷
          </Button>
          <Button
            startIcon={<DownloadIcon />}
            onClick={handleDownload}
          >
            PDF出力
          </Button>
        </Box>
      </Box>

      <Paper sx={{ p: 3 }}>
        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        <Box sx={{ mt: 2, mb: 4 }}>
          {getStepContent(activeStep)}
        </Box>

        <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Button
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            戻る
          </Button>
          <Box>
            {activeStep === steps.length - 1 ? (
              <Button
                variant="contained"
                onClick={handleSave}
              >
                完了
              </Button>
            ) : (
              <Button
                variant="contained"
                onClick={handleNext}
              >
                次へ
              </Button>
            )}
          </Box>
        </Box>
      </Paper>
    </Box>
  );
};

export default PlanEditor;
